// Object Literal
let obj = {
    name: "antim",
    age : 22
}
console.log(obj)  //{ name: 'antim', age: 22 }

let object = new Object();
object.name = "antim";
object.age  = 22
console.log(object)  //{ name: 'antim', age: 22 }




let arr = ['red', 'green', 'blue'];
console.log(arr[0]) //red

let array = new Array('red', 'green', 'blue');
console.log(array[0]) //red



